mapper.select:
SELECT *
FROM ADMIN
WHERE ADMIN_ID = #{adminId} AND ADMIN_PASSWORD = #{adminPassword}

WITH DateRange AS ( SELECT TRUNC(SYSDATE) - LEVEL + 1 AS registration_date FROM DUAL
CONNECT BY LEVEL <= 7
) SELECT TO_CHAR(DateRange.registration_date, 'MM-DD') AS month_day, NVL(user_counts.daily_user_count, 0) AS daily_user_count FROM DateRange LEFT JOIN ( SELECT TO_CHAR(TRUNC(register_date), 'MM-DD') AS registration_date, COUNT(*) AS daily_user_count FROM users WHERE register_date >= TRUNC(SYSDATE) - 7 GROUP BY TO_CHAR(TRUNC(register_date), 'MM-DD') ) user_counts ON TO_CHAR(DateRange.registration_date, 'MM-DD') = user_counts.registration_date ORDER BY DateRange.registration_date;

SELECT rnum, USER_NUMBER, USER_ID, USER_PASSWORD, USER_NAME, USER_EMAIL, TO_CHAR(REGISTER_DATE, 'YYYY-MM-DD') AS REGISTER_DATE, review FROM ( SELECT ROWNUM AS rnum, USER_NUMBER, USER_ID, USER_PASSWORD, USER_NAME, USER_EMAIL, REGISTER_DATE,review FROM ( SELECT U.USER_NUMBER, U.USER_ID, U.USER_PASSWORD, U.USER_NAME, U.USER_EMAIL, U.REGISTER_DATE,COALESCE(S.SITTER_COUNT, 0) + COALESCE(E.EVENT_COUNT, 0) as review FROM USERS U LEFT JOIN ( SELECT USER_NUMBER, COUNT(*) AS SITTER_COUNT FROM SITTER_BOARD GROUP BY USER_NUMBER ) S ON U.USER_NUMBER = S.USER_NUMBER LEFT JOIN ( SELECT USER_NUMBER, COUNT(*) AS EVENT_COUNT FROM EVENT_BOARD GROUP BY USER_NUMBER ) E ON U.USER_NUMBER = E.USER_NUMBER
AND USER_NAME LIKE '%' || #{searchVo.keyword} || '%'
AND USER_ID LIKE '%' || #{searchVo.keyword} || '%'
ORDER BY USER_NUMBER DESC ) where rownum<=#{criteria.page} * #{criteria.amount}
)where rnum> (#{criteria.page}-1)*#{criteria.amount}

SELECT rnum, EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, TO_CHAR(EMP_DATE, 'YYYY-MM-DD') AS EMP_DATE, EMP_CONTENT FROM ( SELECT ROWNUM AS rnum, EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, EMP_DATE, EMP_CONTENT FROM ( SELECT EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, EMP_DATE, EMP_CONTENT FROM EMP
AND EMP_NAME LIKE '%' || #{searchVo.keyword} || '%'
AND EMP_EMAIL LIKE '%' || #{searchVo.keyword} || '%'
ORDER BY EMP_NUMBER DESC ) WHERE rownum <= #{criteria.page} * #{criteria.amount}
) WHERE rnum > (#{criteria.page} - 1) * #{criteria.amount}

SELECT rnum, NOTICE_NUMBER, NOTICE_TITLE, NOTICE_CONTENT,NOTICE_COUNT, TO_CHAR(NOTICE_REGISTER_DATE, 'YYYY-MM-DD') AS NOTICE_REGISTER_DATE , TO_CHAR(NOTICE_MODIFY_DATE,'YYYY-MM-DD') AS NOTICE_MODIFY_DATE FROM ( SELECT ROWNUM AS rnum, NOTICE_NUMBER, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_REGISTER_DATE, NOTICE_MODIFY_DATE, NOTICE_COUNT FROM ( SELECT NOTICE_NUMBER, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_REGISTER_DATE, NOTICE_MODIFY_DATE, NOTICE_COUNT FROM NOTICE
AND notice_title LIKE '%' || #{searchVo.keyword} || '%'
ORDER BY NOTICE_NUMBER DESC ) WHERE rownum <= #{criteria.page} * #{criteria.amount}
) WHERE rnum > (#{criteria.page} - 1) * #{criteria.amount}

mapper.update:
update notice
set NOTICE_CONTENT = #{noticeContent} , NOTICE_TITLE = #{noticeTitle}, NOTICE_MODIFY_DATE = SYSDATE
where NOTICE_NUMBER=#{noticeNumber}

mapper.select:
SELECT rnum,MATCH_NUMBER,STATUS,USER_NUMBER,USER_NAME,USER_ID,USER_PHONE,emp_number,emp_name,emp_email,emp_phone FROM ( select rownum as rnum, MATCH_NUMBER,STATUS,USER_NUMBER,USER_NAME,USER_ID,USER_PHONE,emp_number,emp_name,emp_email,emp_phone from( SELECT M.match_number as match_number, M.status as status,M.user_number as user_number, U.user_name as user_name,U.user_id as user_id,U.user_phone as user_phone, M.emp_number as emp_number,E.emp_name as emp_name,E.emp_email as emp_email,E.emp_phone as emp_phone FROM Matchs M JOIN users U ON M.user_number = U.user_number JOIN EMP E ON M.emp_number = E.EMP_NUMBER
AND user_name LIKE '%' || #{searchVo.keyword} || '%'
AND emp_name LIKE '%' || #{searchVo.keyword} || '%'
ORDER BY match_NUMBER DESC ) WHERE rownum <= #{criteria.page} * #{criteria.amount}
) WHERE rnum > (#{criteria.page} - 1) * #{criteria.amount}

select rnum, event_number, event_name, event_price,
       event_img_name, event_img_upload_path, event_img_uuid,
       event_detail_name, event_detail_upload_path, event_detail_uuid
from(
        select ROWNUM AS RNUM, event_number, event_name, event_price,
               event_img_name, event_img_upload_path, event_img_uuid,
               event_detail_name, event_detail_upload_path, event_detail_uuid
        from (
                 select e.event_number, e.event_name, e.event_price,
                        ei.EVENT_IMG_NAME, ei.EVENT_IMG_UPLOAD_PATH,ei.EVENT_IMG_UUID,
                        ED.EVENT_DETAIL_NAME, ED.EVENT_DETAIL_UPLOAD_PATH, ED.EVENT_DETAIL_UUID
                 from event e
                          LEFT JOIN event_img ei on e.EVENT_NUMBER = ei.event_number
                          LEFT JOIN  EVENT_DETAIL ed on e.EVENT_NUMBER = ed.event_number



                            AND event_name LIKE '%' || #{searchVo.keyword} || '%'



                order by EVENT_NUMBER DESC
                ) 
            WHERE rownum <= #{criteria.page} * #{criteria.amount}

                ) WHERE rnum > (#{criteria.page} - 1) * #{criteria.amount}
