mapper.select:
SELECT MATCH_NUMBER, USER_NUMBER, SUBMIT_ORDER_NUMBER, EMP_NUMBER, STATUS, EST_TITLE, EMP_IMG_NAME, EMP_IMG_UPLOAD_PATH, EMP_IMG_UUID, EST_PRICE , REVIEW_CHECK FROM ( SELECT ROWNUM AS RNUM, P1.MATCH_NUMBER, USER_NUMBER, SUBMIT_ORDER_NUMBER, EMP_NUMBER, STATUS, EST_TITLE, EMP_IMG_NAME, EMP_IMG_UPLOAD_PATH, EMP_IMG_UUID, EST_PRICE , REVIEW_CHECK FROM ( SELECT S1.MATCH_NUMBER, USER_NUMBER, SUBMIT_ORDER_NUMBER, S1.EMP_NUMBER, STATUS , EC.EST_TITLE , EC.EST_PRICE , EI.EMP_IMG_NAME, EI.EMP_IMG_UPLOAD_PATH, EI.EMP_IMG_UUID , (select count(SB.SITTER_BOARD_NUMBER) FROM SITTER_BOARD SB WHERE S1.MATCH_NUMBER = SB.MATCH_NUMBER) AS REVIEW_CHECK FROM ( SELECT match_number, user_number, submit_order_number, emp_number, status FROM MATCHS WHERE USER_NUMBER = #{userNumber} AND STATUS = 2
) S1 left JOIN EST_CONTENT EC ON S1.MATCH_NUMBER = EC.MATCH_NUMBER LEFT JOIN EMP_IMG EI ON S1.EMP_NUMBER = EI.EMP_NUMBER order by match_number desc ) P1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) P2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

select  RNUM,EVENT_PAY_NUMBER ,APPLY_NUMBER,USER_NUMBER, EVENT_NUMBER,EVENT_DATE,
EVENT_NAME, EVENT_PRICE,EVENT_IMG_NAME, EVENT_IMG_UPLOAD_PATH, EVENT_IMG_UUID,REVIEW_CHECK
from(
    select ROWNUM AS RNUM,EVENT_PAY_NUMBER ,APPLY_NUMBER,USER_NUMBER, EVENT_NUMBER,EVENT_DATE,
    EVENT_NAME, EVENT_PRICE,EVENT_IMG_NAME, EVENT_IMG_UPLOAD_PATH, EVENT_IMG_UUID,REVIEW_CHECK
    from(
        SELECT E1.APPLY_NUMBER,E2.EVENT_PAY_NUMBER ,USER_NUMBER, e1.EVENT_NUMBER,EVENT_DATE,
        E3.EVENT_NAME, E3.EVENT_PRICE,EI.EVENT_IMG_NAME, EI.EVENT_IMG_UPLOAD_PATH, EI.EVENT_IMG_UUID
        ,(SELECT COUNT(EB.EVENT_NUMBER) FROM EVENT_BOARD EB WHERE EB.EVENT_NUMBER =e1.EVENT_NUMBER) AS REVIEW_CHECK
        FROM(
            SELECT APPLY_NUMBER,USER_NUMBER,EVENT_NUMBER,EVENT_DATE
            FROM EVENT_APPLY
            WHERE USER_NUMBER = #{userNumber}
            )E1
        JOIN EVENT_PAY E2 ON E1.APPLY_NUMBER = E2.APPLY_NUMBER
        JOIN EVENT E3 ON E1.EVENT_NUMBER = E3.EVENT_NUMBER
        left JOIN EVENT_IMG EI ON E3.EVENT_NUMBER = EI.EVENT_NUMBER
        ORDER BY EVENT_NUMBER DESC
        )

    where ROWNUM <= #{criteria.page} * #{criteria.amount}

)
where RNUM > (#{criteria.page} - 1) * #{criteria.amount}

select COUNT(EP.APPLY_NUMBER) AS PAYTOTAL
from(
        SELECT APPLY_NUMBER
        FROM EVENT_APPLY
        WHERE USER_NUMBER = #{userNumber}
    )S1
        JOIN EVENT_PAY EP ON S1.APPLY_NUMBER=EP.APPLY_NUMBER

select  em.emp_number,emp_name,emp_age,emp_gender,emp_phone,emp_email,emp_date,emp_content, emp_img_name,emp_img_upload_path,emp_img_uuid
from(
        select emp_number,emp_name,emp_age,emp_gender,emp_phone,emp_email,emp_date,emp_content
        from emp
        where EMP_NUMBER =#{empNumber}
    ) em
        join EMP_IMG ei on em.EMP_NUMBER=ei.EMP_NUMBER

select em2.emp_number,em2.emp_act_item_number,em2.act_number,ACT_IMG_NUMBER,act_img_name,act_img_upload_path,act_img_uuid,act_name
from (
         select em1.ACT_NUMBER, act_name, EMP_ACT_ITEM_NUMBER,EMP_NUMBER
         from (
                  select em.EMP_NUMBER, EMP_ACT_ITEM_NUMBER, act_number
                  from (
                           select emp_number,
                                  emp_name,
                                  emp_age,
                                  emp_gender,
                                  emp_phone,
                                  emp_email,
                                  emp_date,
                                  emp_content
                           from emp
                           where EMP_NUMBER = #{empNumber}
                       ) em
                           join emp_act_item aci on em.EMP_NUMBER = aci.EMP_NUMBER
              ) em1
                  join act a on em1.ACT_NUMBER = a.ACT_NUMBER
     )em2
         join act_img ai on em2.ACT_NUMBER=ai.ACT_NUMBER

select distinct (select avg(RATING) from SITTER_BOARD) as avg
from SITTER_BOARD
where EMP_NUMBER=#{empNumber}

select s1.match_number, user_number, submit_order_number, emp_number, status,EST_CONTENT,EST_TITLE,EST_PRICE
from (
         select match_number, user_number, submit_order_number, emp_number, status
         from MATCHS
         where USER_NUMBER = #{userNumber}
           and STATUS = '1'
     )s1
         join EST_CONTENT sc on s1.MATCH_NUMBER = sc.MATCH_NUMBER

mapper.update:
update MATCHS
set  status= 2
where match_number=#{matchNumber}

mapper.select:
select USER_NUMBER, USER_ID, USER_PASSWORD, USER_NAME, USER_EMAIL, USER_PHONE
from users
where user_number =#{userNumber}

select ADDRESS_NUMBER, USER_NUMBER, ADDRESS_POST, ADDRESS, ADDRESS_DETAIL
from ADDRESS
where user_number =#{userNumber}
