mapper.select:
select rnum, SITTER_BOARD_NUMBER, user_number, user_id, EMP_NUMBER,SITTER_BOARD_CONTENT, SITTER_BOARD_REGISTER_DATE, SITTER_BOARD_MODIFY_DATE, RATING, SITTER_BOARD_COUNT, EMP_NAME, emp_img_UPLOAD_PATH, emp_img_UUID, emp_img_NAME from( select rownum as rnum, SITTER_BOARD_NUMBER, user_number, user_id, EMP_NUMBER,SITTER_BOARD_CONTENT, SITTER_BOARD_REGISTER_DATE, SITTER_BOARD_MODIFY_DATE, RATING, SITTER_BOARD_COUNT, EMP_NAME, emp_img_UPLOAD_PATH, emp_img_UUID, emp_img_NAME from ( select s.SITTER_BOARD_NUMBER, s.user_number, u.user_id,s.EMP_NUMBER,SITTER_BOARD_CONTENT, SITTER_BOARD_REGISTER_DATE, SITTER_BOARD_MODIFY_DATE, RATING, SITTER_BOARD_COUNT, e.EMP_NAME, i.emp_img_UPLOAD_PATH, i.emp_img_UUID, i.emp_img_NAME from SITTER_BOARD s join users u on s.user_number = u.user_number join emp e on s.EMP_NUMBER = e.EMP_NUMBER left outer join EMP_IMG i on e.EMP_NUMBER = i.emp_number
where emp_name Like '%'|| #{searchReviewVo.keyword} ||'%' order by SITTER_BOARD_NUMBER desc )
where rownum<= #{criteria.page} * #{criteria.amount}
) where rnum> (#{criteria.page}-1) * #{criteria.amount}

select avg(rating)
from SITTER_BOARD
where EMP_NUMBER = #{empNumber}

update sitter_board
set sitter_board_count = sitter_board_count +1
where sitter_board_number = #{sitterBoardNumber}

mapper.update:
update sitter_board
set sitter_board_content = #{sitterBoardContent}, sitter_board_modify_date = sysdate, rating = #{rating}
where sitter_board_number = #{sitterBoardNumber}

mapper.select:
select rnum, event_BOARD_NUMBER, user_number, user_id, event_NUMBER, event_BOARD_CONTENT, event_BOARD_REGISTER_DATE, event_BOARD_MODIFY_DATE, RATING, event_BOARD_COUNT, event_NAME,event_board_img_UPLOAD_PATH, event_board_img_UUID, event_board_img_NAME from ( select rownum as rnum, event_BOARD_NUMBER, user_number, user_id, event_NUMBER, event_BOARD_CONTENT, event_BOARD_REGISTER_DATE, event_BOARD_MODIFY_DATE, RATING, event_BOARD_COUNT, event_NAME,event_board_img_UPLOAD_PATH, event_board_img_UUID, event_board_img_NAME from ( select s.event_BOARD_NUMBER, s.user_number, u.user_id, s.event_NUMBER, event_BOARD_CONTENT, event_BOARD_REGISTER_DATE, event_BOARD_MODIFY_DATE, RATING, event_BOARD_COUNT, e.event_NAME, event_board_img_UPLOAD_PATH, event_board_img_UUID, event_board_img_NAME from EVENT_Board s join users u on s.user_number = u.user_number join event e on s.event_NUMBER = e.event_NUMBER left outer join event_board_IMG i on s.EVENT_board_NUMBER = i.EVENT_board_NUMBER
where event_name Like '%'|| #{searchReviewVo.keyword} ||'%' order by event_BOARD_NUMBER desc
)
where rownum <=#{criteria.page} * #{criteria.amount} ) where rnum>(#{criteria.page}-1) * #{criteria.amount}

select rnum, event_BOARD_NUMBER, user_number, user_id, event_NUMBER, event_BOARD_CONTENT, event_BOARD_REGISTER_DATE, event_BOARD_MODIFY_DATE, RATING, event_BOARD_COUNT, event_NAME,event_board_img_UPLOAD_PATH, event_board_img_UUID, event_board_img_NAME from ( select rownum as rnum, event_BOARD_NUMBER, user_number, user_id, event_NUMBER, event_BOARD_CONTENT, event_BOARD_REGISTER_DATE, event_BOARD_MODIFY_DATE, RATING, event_BOARD_COUNT, event_NAME,event_board_img_UPLOAD_PATH, event_board_img_UUID, event_board_img_NAME from ( select img.event_BOARD_NUMBER, s.user_number, u.user_id, s.event_NUMBER, event_BOARD_CONTENT, event_BOARD_REGISTER_DATE, event_BOARD_MODIFY_DATE, RATING, event_BOARD_COUNT, e.event_NAME,event_board_img_UPLOAD_PATH, event_board_img_UUID, event_board_img_NAME from EVENT_Board s join users u on s.user_number = u.user_number join event e on s.event_NUMBER = e.event_NUMBER left outer join event_board_IMG img on s.EVENT_board_NUMBER = img.EVENT_board_NUMBER
order by event_BOARD_count desc
) ) where rnum between 1 and 6

select count(event_board_number) from ( select event_BOARD_NUMBER from event_BOARD s join event e on s.event_number = e.event_NUMBER
where event_name Like '%'|| #{keyword} ||'%'
)

select Avg(rating)
from event_Board
where event_number = #{eventNumber}

mapper.update:
update event_board
set event_board_content = #{eventBoardContent}, event_board_modify_date = sysdate, rating = #{rating}
where event_board_number = #{eventBoardNumber}

update event_board
set event_board_count = event_board_count +1
where event_board_number = #{eventBoardNumber}
